mapper.insert:
insert into
bookmark
(
bookmark_num
,member_id
,contents_num

) values (
bookmark_seq.nextval
,#{member_id}
,#{contents_num}

)

mapper.select:
select bookmark_num ,member_id ,contents_num ,to_char(inputdate, 'yyyy/MM/dd') inputdate
from bookmark
where member_id = #{member_id}
and contents_num = #{contents_num}
order by bookmark_num desc

mapper.delete:
delete
	bookmark

where
	member_id = #{member_id}

and
	contents_num = #{contents_num}
